import {
  Box,
  ChakraProvider,
  Heading,
  Container,
  Text,
  Input,
  Button,
  Wrap,
  Stack,
  Image,
  Link,
  SkeletonCircle,
  SkeletonText,
} from "@chakra-ui/react";
import axios from "axios";
import { useState } from "react";

import "./App.css";

function App() {
  const [image, setImage] = useState(null); // Initialize with null
  const [prompt, updatePrompt] = useState("");
  const [loading, updateLoading] = useState(false); // Initialize with false

  const generate = async (prompt) => {
    updateLoading(true);
    try {
      const response = await axios.get(
        `http://127.0.0.1:8000/?prompt=${prompt}`
      );
      const imageData = response.data.image; // Access the 'image' field from the response
      setImage(`data:image/png;base64,${imageData}`); // Construct the base64 data URL
    } catch (error) {
      console.error("Error generating image:", error);
    }
    updateLoading(false);
  };

  return (
    <ChakraProvider>
      <Container width={"100%"}>
        <Heading marginTop={"4rem"} marginBottom={"1rem"}>
          Image Generator AIðŸš€
        </Heading>
        <Text marginBottom={"1rem"}>
          This react application leverages the model trained by Stability AI and
          Runway ML to generate images using the Stable Diffusion Deep Learning
          model. The model can be found via github here{" "}
          <Link href={"https://github.com/CompVis/stable-diffusion"} isExternal>
            Github Repo
          </Link>
        </Text>

        <Wrap marginBottom={"10px"}>
          <Input
            value={prompt}
            onChange={(e) => updatePrompt(e.target.value)}
            width={"350px"}
            placeholder="Enter prompt"
          />
          <Button onClick={() => generate(prompt)} colorScheme={"yellow"}>
            Generate
          </Button>
        </Wrap>
        {loading ? (
          <Stack>
            <SkeletonCircle />
            <SkeletonText />
          </Stack>
        ) : image ? (
          <Image
            src={image}
            boxShadow="lg"
            alt="Generated"
            borderRadius={"10px"}
            margin={"20px"}
          />
        ) : (
          <Text>No image generated yet.</Text>
        )}
      </Container>
    </ChakraProvider>
  );
}

export default App;
