import {
  ChakraProvider,
  Heading,
  Container,
  Text,
  Input,
  Button,
  Wrap,
  Stack,
  Image,
  Link,
  SkeletonCircle,
  SkeletonText,
} from "@chakra-ui/react";
import axios from "axios";
import { useState } from "react";

import "./App.css"; // Import your CSS file for styles

function App() {
  const [image, setImage] = useState(null);
  const [prompt, updatePrompt] = useState("");
  const [loading, updateLoading] = useState(false);

  const generate = async (prompt) => {
    updateLoading(true);
    try {
      const response = await axios.get(
        `http://127.0.0.1:8000/?prompt=${prompt}`
      );
      const imageData = response.data.image;
      setImage(`data:image/png;base64,${imageData}`);
    } catch (error) {
      console.error("Error generating image:", error);
    }
    updateLoading(false);
  };

  return (
    <ChakraProvider>
      <div className="App-container">
        <Container>
          <Heading
            marginTop={"4rem"}
            marginBottom={"1rem"}
            className="heading-animate"
            textAlign={"center"}
          >
            AI-Powered Image Generator ðŸš€
          </Heading>
          <Text marginBottom={"1rem"} className="fade-in">
            Explore the capabilities of AI with this application that generates
            images from textual prompts using the advanced Stable Diffusion
            model developed by Stability AI and Runway ML. Check out the model's
            source code on{" "}
            <Link
              href={"https://github.com/CompVis/stable-diffusion"}
              isExternal
            >
              GitHub
            </Link>
            .
          </Text>

          <Wrap marginBottom={"10px"} className="input-animate">
            <Input
              value={prompt}
              onChange={(e) => updatePrompt(e.target.value)}
              width={"350px"}
              placeholder="Enter prompt"
              sx={{
                border: "2px solid",
                borderImageSlice: 1,
                borderImageSource:
                  "linear-gradient(to right, blue.400, purple.500)",
              }}
            />
            <Button
              onClick={() => generate(prompt)}
              bgGradient="linear(to-r, blue.400, purple.500)"
              color="white"
              className="button-animate"
              _hover={{
                bgGradient: "linear(to-r, blue.500, purple.600)",
              }}
            >
              Generate
            </Button>
          </Wrap>
          {loading ? (
            <Stack>
              <SkeletonCircle />
              <SkeletonText />
            </Stack>
          ) : image ? (
            <Image
              src={image}
              boxShadow="lg"
              alt="Generated"
              borderRadius="md"
              className="fade-in"
            />
          ) : (
            <Text>No image generated yet.</Text>
          )}
        </Container>
      </div>
    </ChakraProvider>
  );
}

export default App;
